import React from "react";

const Quote = ({ loading, advice }) => {
  return (
    <div className="elements">
      {loading ? (
        <div className="loading">...Loading</div>
      ) : (
        <div className="quote">{advice}</div>
      )}
    </div>
  );
};

export default Quote;
