import React from "react";
import { useState, useEffect } from "react";

const APIURL = "https://api.adviceslip.com/advice";

const Quote = () => {
  const [advice, setAdvice] = useState([]);

  const getAdvice = (API) => {
    fetch(API)
      .then((res) => res.json())
      .then((data) => {
        setAdvice(data.slip);
        console.log(data);
      });
  };

  useEffect(() => {
    getAdvice(APIURL);
  }, []);

  return <div className="quote">{advice}</div>;
};

export default Quote;
